mapper.update:
UPDATE TBL_ADDRESS
SET SAVEYN = 'N'
WHERE USER_ID = #{userId} AND ADDRESS_NO = #{addressNo}

UPDATE TBL_ADDRESS
SET DEFAULTYN = 'N'
WHERE USER_ID = #{userId}

			UPDATE TBL_BOOK
			SET QUANTITY = QUANTITY - CASE

				WHEN BOOK_ID = #{item.bookId} THEN #{item.bookQuantity}

			END
			WHERE BOOK_ID IN (

        		#{item.bookId}

 			)

mapper.delete:
DELETE FROM TBL_CART
WHERE USER_ID = #{userId} AND BOOK_ID IN 

#{item.bookId}

mapper.select:
SELECT OI.BOOK_ID, OI.QUANTITY, O.STATUS, RI.STATUS AS REFUND_STATUS ,CASE
WHEN RI.BOOK_ID = #{item.bookId} THEN OI.QUANTITY - RI.QUANTITY - #{item.bookQuantity}
ELSE OI.QUANTITY END AS CALC FROM TBL_ORDER O LEFT JOIN TBL_ORDER_ITEM OI ON O.ORDER_ID = OI.ORDER_ID LEFT JOIN TBL_REFUND R ON R.ORDER_ID = O.ORDER_ID LEFT JOIN TBL_REFUND_ITEM RI ON R.REFUND_ID = RI.REFUND_ID WHERE O.ORDER_ID = #{orderId}

mapper.update:
UPDATE TBL_ORDER
SET STATUS = #{newStatus}
WHERE ORDER_ID = #{orderId}

mapper.select:
SELECT OI.BOOK_ID, RI.STATUS AS STATUS, OI.PRICE ,CASE
WHEN RI.BOOK_ID = #{item.bookId} THEN #{item.quantity}
ELSE 1 END AS QUANTITY FROM TBL_REFUND_ITEM RI LEFT JOIN TBL_REFUND R ON R.REFUND_ID = RI.REFUND_ID LEFT JOIN TBL_ORDER O ON R.ORDER_ID = O.ORDER_ID LEFT JOIN TBL_ORDER_ITEM OI ON O.ORDER_ID = OI.ORDER_ID WHERE R.REFUND_ID = #{refundId} AND RI.STATUS = '승인됨'
