mapper.select:
SELECT *
FROM TBL_CART
WHERE USER_ID = #{userId} AND BOOK_ID = #{bookId}

mapper.update:
UPDATE TBL_CART
SET QUANTITY = QUANTITY + #{quantity}
WHERE USER_ID = #{userId} AND BOOK_ID = #{bookId}

UPDATE TBL_CART
SET QUANTITY = #{quantity}
WHERE USER_ID = #{userId} AND BOOK_ID = #{bookId}

